        /**
         * bubble sort
         * @param nums 
         * @returns nums
         */
        public bubbleSort(nums: number[]) : number[]{
    
            // 配列の長さの取得
            const numsLen:number = nums.length;
            // カウンタの宣言
            let i: number = 0, j:number = 0;
            // 配列の要素数分ループ ex). nums = [0, 1, 2] -> ループ3
            for (; i < numsLen; i++ ) {
                // 配列の右端の要素からループを行う ex). nums = [0, 1, 2]
                for (j = numsLen - 1; j > i; j--) {
                    // 右端の要素と隣接する要素との比較
                    if(nums[j] < nums[j - 1]) {
                        // 隣接する要素の方が多き場合、要素の値を入替える
                        let tmp:number = nums[j - 1];
                        nums[j - 1] = nums[j];
                        nums[j] = tmp;
                    }
                }
            }
            return nums;
        }

        /**
         * quickSortV1
         * @param nums
         * @returns nums
         */
        public quickSortV1(nums: number[]): number[]{

            function partition(start: number, end: number){
                if (start >= end) return;
                // グルーピングの軸となる値
                let pviot: number = nums[start];
                // グループの最も左端のIndex
                let left: number = start;

                for (let i = start + 1; i <= end; i++) {
                    if (nums[i] < pviot) {
                        
                        // 配列の値の入れ替え
                        let temp: number = nums[i]; //swap
                        nums[i] = nums[++left];
                        nums[left] = temp;
    
                    }
                }
                // 配列の値の入れ替え
                let temp: number = nums[left]; //swap
                nums[left] = nums[start];
                nums[start] = temp;
                
                // 再帰処理
                partition(start, left - 1);
                partition(left + 1, end);
            }
            // main処理の呼び出し
            partition(0, nums.length - 1);
            return nums;
        }

